
public class DataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Two types of data i.e primitive and non-primitive 
		// String,Array,Class are non-primitive 
		// Primitive data type >>> only 8 type >> 
		// byte, Short, int, long, float, double, boolean and char
		
		
		byte myByte = 126; //byte ranges from -128 to 127
		
			 // Variable   Value
		short myShort   = -32768; //Short range from -32,768 to 32,767
		
		int myInt = 2147483646; // int range from -2,147,483,648 to -2,147,483,647
		
		long myLong = 214748364765368590L; // long range from -9,223,372,775,808 to 9,223,372,775,807  have to enter sufix L 
		
		float myFloat = .1234567F;       //Float stories fractional numbers up to 6 decimal digits
		
		double myDouble =.123456782456;    //double stories fractional numbers up to 15 decimal digits
				
		boolean myBoolean = false;        //By default value it's false or true or yes or no 
		
		char myChar = 'R';       //char contains only character have to have ' ' 
		
		System.out.println("My Byte is: "+ myByte); // Using string must use + and ""
		System.out.println("My Short is: "+ myShort);
		System.out.println("My Int is: "+ myInt);
		System.out.println("My Long is: "+ myLong);
		System.out.println("My Float is: "+ myFloat);
		System.out.println("My Double is: "+ myDouble);
		System.out.println("My Boolean is: "+ myBoolean);
		System.out.println("My Char is: "+ myChar);
		
		
		
		
	}

}
