
public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int myValue = (int)(Math.random()*200);
		
		System.out.println(myValue);
		
		double num = 8.54;
		System.out.println(num); 
		// typecasting
		int myIntnum =(int)(num);
		System.out.println(myIntnum);
		
		/* Operators
		 * Operators --> Operators perform actions on variable and values
		 * Arithmetic Operators // + ,* , -, /devision, 
		 * % Modules, // Returns to division remainder 
		 *  ++ Increment, // increase the value of variable by 1 -> ++x
		 *   -- Decrement // decreases the value of a variable by 1 -> --y
		 * Assignments Operators 
		 * Comparison/Relational operators 
		 * Logical operators
		 */
		
		
		
		
		
	}

}
